import React, { useEffect, useState } from "react";
import TodoList from "./Todo/TodoList";
import Context from "./context";
import AddTodo from "./Todo/AddTodo";
//import { bool } from "prop-types";

const App = () => { 
  const [todos, setTodos] = useState([]);
  const [filter, setFilter] = useState('all');

  useEffect(() => {  
    fetch('http://127.0.0.1:8000/api/v1/todos/todos/')
    .then(response => response.json())
    .then((data) => {     
      setTodos(checkFilter(data, filter));    
    }) 
  }, []);

  useEffect(() => {
    setTodos(checkFilter(todos, filter));
  }, [filter])

  useEffect(() => {
    if (todos.status){
    postData('http://127.0.0.1:8000/api/v1/todos/todos/'+todos.id+'/', 'UPDATE', todos.id); 
    console.log(v)  
    }
  }, [todos.status])

  const toggleTodo = (id) => {  
    setTodos(todos.map(todo => {
      if (todo.id === id) {
        todo.status = !todo.status;
      }      
      return todo;
    }));
  }  

  const checkFilter = (data, todoFilter) => {
      switch (todoFilter) {
        case 'all':  
          return data;       
        case 'work': 
          return data.filter(todo => !todo.status);
        case 'compl': 
          return data.filter(todo => todo.status);
        default:
          break; 
      }
  }

  const removeTodo = (id) => {     
    postData('http://127.0.0.1:8000/api/v1/todos/todos/'+id+'/', 'DELETE', {id}); 
    setTodos(todos.filter(todo => todo.id !== id));  
  }

  const clearAll = () => {             
    todos.forEach(e => {
      removeTodo(e.id)        
    })
    setTodos([]);
    setFilter('all');
  }

  const showWork = () => {    
    setTodos(todos.filter(todo => !todo.status));
    setFilter('work');
  }

  const showComplited = () => {    
    setTodos(todos.filter(todo => todo.status));
    setFilter('compl');
  }

  const showAll = () => { 
    console.log('tut', )
    postData('http://127.0.0.1:8000/api/v1/todos/todos/','GET')
    .then((data) => {setTodos(data)})
    setFilter('all');
  }

  const addTodo = (text) => {
    postData('http://127.0.0.1:8000/api/v1/todos/todos/', 'POST', {text})
    .then((data) => {
      setTodos(todos.concat([{              
        id:data.id,
        text,
        status: false,
      }]));
    });      
  }

  async function postData(url, meth, data ) {
    // Default options are marked with *
    const response = await fetch(url, {
    method: meth, // *GET, POST, PUT, DELETE, etc.  
    headers: {
      'Content-Type': 'application/json;charset=utf-8',      
    },
    body: JSON.stringify(data), // body data type must match "Content-Type" header
    dataType: 'json',
    });
    if (meth !== 'DELETE' && meth !== 'UPDATE')
      return await response.json(); // parses JSON response into native JavaScript objects
  }

  return (    
    <>   
      <Context.Provider value = {{removeTodo}}>
      <div>        
          <div className = 'wrapper'>
            <h1>ToDo List</h1>
            <AddTodo onCreate = {addTodo}/>
            {todos.length ? <TodoList todos = {todos} onToggle = {toggleTodo}/> : <p>no Todo</p>}    
          </div>          
        <div id ='btnPress'> 
          <button  type='submit' onClick = {clearAll} className = 'btnStates'>remove all</button>           
          <button id = 'btnShowAll' type='submit' onClick = {showAll} className = {`btnStates ${filter === 'all' ? 'gradient-button' : ''}`}>show all</button> 
          <button id = 'btnshowWork' type='submit' onClick = {showWork} className = {`btnStates ${filter === 'work' ? 'gradient-button' : ''}`}>show in worke</button> 
          <button id = 'btnShowComp' type='submit' onClick = {showComplited} className = {`btnStates ${filter === 'compl' ? 'gradient-button' : ''}`}>show complited</button>  
        </div> 
        </div>   
      </Context.Provider>
    </>
  );

}

export default App;
