import React, { useEffect, useState } from "react";
import TodoList from "./Todo/TodoList";
import Context from "./context";
import AddTodo from "./Todo/AddTodo";


const App = () => { 
  const [todos, setTodos] = useState([]);
   const [filter, setFilter] = useState('all');

  useEffect(() => {
    if (localStorage.length > 0) {
      let locArr = JSON.parse(localStorage.getItem('key'));     
      let arr = [...locArr];
      setTodos(arr);      
      }    
  }, [])

  useEffect(() => {
    console.log(todos);
  }, [todos])

  const toggleTodo = (id) => {  

    let localArr = [...todos]
    let a = localArr.map( todo => {
      if (todo.id === id){
        todo.completed = !todo.completed
      }      
    return todo
    });
    updateLocalStorege(a); 
    setTodos(a);
    // switch (filter){
    //   case 'all': 
    //     showeAll();
    //     break;
    //   case 'work': 
    //     showeWork();
    //     break;
    //   case 'compl': 
    //     showeComplited();
    //     break; 
    //   default:
    //     break; 
    // }
  }  

const removeTodo = (id) => {  
  let localArr = [...todos]
  let a = localArr.filter(localArr => localArr.id !== id)  
  setTodos(a);
  updateLocalStorege(a);
}

const clearAll= () => {
  localStorage.setItem('key', JSON.stringify([]));  
  setTodos([]);
}

const showeWork = (id) => { 
  let localArr = pushTodos();
  setTodos(localArr.filter(todo => todo.completed !== true));
  // setFilter('work');
}

const showeComplited = (id) => {
  let localArr = pushTodos();
  setTodos(localArr.filter(todo => todo.completed === true));
  // setFilter('compl');
}

const showeAll = () => {
  let localArr = pushTodos();
  setTodos(localArr);
  // setFilter('all');
}

const addTodo = (title) => {
  let arrLocal = [...todos]
  let a = arrLocal.concat([{
    title,
    id: Date.now(),
    completed:false,
  }]);  
  setTodos(a);
  updateLocalStorege(a); 
}

const updateLocalStorege = (a) => {     
  localStorage.setItem('key', JSON.stringify(a));  
}

const pushTodos = () => {
  let arr=[];
  if (localStorage.length > 0) {
    let locArr = JSON.parse(localStorage.getItem('key'));     
    arr = [...locArr];       
  }
  return arr; 
}

  return (
    <React.Fragment>   
    <Context.Provider value = {{removeTodo}}>
      <div className='btnStates1'>
    <div className = 'wrapper'>
      <h1>React tutorial</h1>
      <AddTodo onCreate={addTodo}/>
      {todos.length ? <TodoList todos={todos} onToggle={toggleTodo}/> : <p>no Todo</p>}    
    </div>
    <div className = 'btnStates'>  
     <button type='submit' onClick = {clearAll}>remove all</button> 
     <button type='submit' onClick = {showeAll}>showe all</button> 
     <button type='submit' onClick = {showeWork}>showe in worke</button> 
     <button type='submit' onClick = {showeComplited} >showe complited</button>  
     </div> 
     </div>    
    </Context.Provider>
    </React.Fragment>
  );
}

export default App;
