import React from "react";
import TodoList from "./Todo/TodoList";
import Context from "./context";
import AddTodo from "./Todo/AddTodo";


const App = () => {

  componen

  const [todos, setTodos] = React.useState([])

  // if (localStorage.length > 0) {
  //   let locArr = JSON.parse(localStorage.getItem('key'));     
  //   let arr = [...locArr];
  //   setTodos(arr);
  // }
  //*заполнить из локалсторедж*/
  const toggleTodo = (id) => {
    console.log('todos4 ',todos);
    setTodos(
      todos.map( todo => {
      if (todo.id === id){
        todo.completed = !todo.completed    
      }
    return todo
    }));
  }

const removeTodo = (id) => {
  setTodos(todos.filter(todo => todo.id !== id))
}

const clearAll= () => {
  setTodos([]);
}

const showeWork = (id) => { 
  pushTodos();
  setTodos( todos.filter(todo => todo.completed !== true));
  /* к кнопке привязать   */
}/* показать все через локалсторедж , стрелочные сделать,  убрать ченджтуду или проктинуть её.*/

const showeComplited = (id) => {
  pushTodos();
  setTodos(todos.filter(todo => todo.completed === true));
}

const showeAll = () => {
  pushTodos();
  setTodos(todos.filter(todo => (todo.completed === true || todo.completed !== true)));
}

const addTodo = (title) => {
  let arrLocal = [...todos]
  let a = arrLocal.concat([{
    title,
    id: Date.now(),
    completed:false,
  }]);  
  setTodos(a);
  localSt(a);
 console.log('todos3 ',a);
}

const localSt = (a) => {        
  localStorage.setItem('key', JSON.stringify(a));
  console.log('todos000 ',a);
}

const pushTodos = () => {
  if (localStorage.length > 0) {
    let locArr = JSON.parse(localStorage.getItem('key'));     
    let arr = [...locArr];
    setTodos(arr);
  }
}
  return (
    <React.Fragment>  
    <Context.Provider value = {{removeTodo}}>
    <div className = 'wrapper'>
      <h1>React tutorial</h1>
      <AddTodo onCreate={addTodo}/>
      {todos.length ? <TodoList todos={todos} onToggle={toggleTodo}/> : <p>no Todo</p>}    
    </div>
    <div className = 'btnStates'>  
     <button type='submit' onClick = {clearAll}>remove all</button> 
     <button type='submit' onClick = {showeAll}>showe all</button> 
     <button type='submit' onClick = {showeWork}>showe in worke</button> 
     <button type='submit' onClick = {showeComplited} >showe complited</button>  
     </div>     
    </Context.Provider>
    </React.Fragment>
  );
}

export default App;
