import React from "react";
import TodoList from "./Todo/TodoList";
import Context from "./context";
import AddTodo from "./Todo/AddTodo";


function App() {
  const [todos, setTodos] = React.useState([
    {id:1, completed:false, title:'Kupit hleb'},
    {id:2, completed:false, title:'Kupit bred'},
    {id:3, completed:false, title:'Kupit moloko'},
  ])
function toggleTodo(id) {
  setTodos(
    todos.map( todo => {
    if (todo.id === id){
      todo.completed = !todo.completed    
    }
    return todo
  }))

}

function removeTodo(id){
  setTodos(todos.filter(todo => todo.id !== id))
}
function clearAll(){
  setTodos.caller();
}
function showeWork(id) {
  return todos.filter(todo => todo.c);
}

function addTodo(title){
  setTodos(todos.concat([{
    title,
    id: Date.now(),
    completed:false,
  }]))

}

  return (
    <React.Fragment>
    <Context.Provider value = {{removeTodo}}>
    <div className='wrapper'>
      <h1>React tutorial</h1>
      <AddTodo onCreate={addTodo}/>
      {todos.length ? <TodoList todos={todos} onToggle={toggleTodo}/> : <p>no Todo</p>}    
    </div>
    </Context.Provider>
    <Context.Provider value = {{showeWork}}>

    </Context.Provider>
    </React.Fragment>
  );
}

export default App;
