import React, { useEffect, useState } from "react";
import TodoList from "./Todo/TodoList";
import Context from "./context";
import AddTodo from "./Todo/AddTodo";
//import { bool } from "prop-types";

const App = () => { 
  const [todos, setTodos] = useState([]);
  const [filter, setFilter] = useState('all');

  // useEffect(() => {  
  //   setTodosFromDB(); 
  //   console.log('start app')          
  // }, []);

  useEffect(() => {
    setTodosFromDB();        
  }, [filter])

/**общие: не работает обновление дб, не работает вывод по группам после первого раза, юз эффект на событие 
 смены фильтра работает лишь 1н раз, первый. решение, пикрутить к каждому вызову отрисовку из ДБ так как меняется тудус после фильтров, ДБ при этом неизменна
 как отлавливать в реалтайме изменения - хз, надо глянут чистый реакт. что ещё не работает? разобраться как работает апдейт, и почему он не работает. 
 хорошо работает вывод всех результатов. аппдейт на локальный чейндж привязать (чуть ниже)
 */
  const toggleTodo = (id) => { 

    //console.log('todos', todos) 

    todos.forEach(todo => {      
      if (todo.id === id) {        
        todo.status = !todo.status;
        workWithDB('http://127.0.0.1:8000/api/v1/todos/todos/'+todo.id+'/', 'PUT',todo);
      }       
    });       
    setTodosFromDB();

   // console.log('todos befor', todos)    

  }  

  const checkFilter = (data, todoFilter) => {
    console.log('filter', filter)
      switch (todoFilter) {
        case 'all':  
          return data;       
        case 'work': 
          return data.filter(todo => !todo.status);
        case 'compl':          
          return data.filter(todo => todo.status);
        default:
          break; 
      }
  }

  const removeTodo = (id) => {     
    workWithDB('http://127.0.0.1:8000/api/v1/todos/todos/'+id+'/', 'DELETE', {id}); 
    setTodos(todos.filter(todo => todo.id !== id));  
  }

  const clearAll = () => {             
    todos.forEach(e => {
      removeTodo(e.id)        
    })
    setTodos([]);
    setFilter('all');
  }

  const showWork = () => {  
    setFilter('work');  
  }

  const showComplited = () => {  
    setFilter('compl');   
  }

  const showAll = () => {     
    setFilter('all');      
  }

  const addTodo = (text) => {
    workWithDB('http://127.0.0.1:8000/api/v1/todos/todos/', 'POST', {text})
    .then((data) => {
      setTodos(todos.concat([{              
        id:data.id,
        text,
        status: false,
      }]));
    });      
  }

  async function workWithDB(url, meth, data ) {
    // Default options are marked with *
    const response = await fetch(url, {
    method: meth, // *GET, POST, PUT, DELETE, etc.  
    headers: {
      'Content-Type': 'application/json;charset=utf-8',      
    },
    body: JSON.stringify(data), // body data type must match "Content-Type" header
    dataType: 'json',
    });
    if (meth !== 'DELETE' && meth !== 'PUT')
      return await response.json(); // parses JSON response into native JavaScript objects
  }

  const setTodosFromDB = () => {
    function callb (data, callback) {
      callback(setTodos(checkFilter(data, filter)))      
    }
    console.log('setTodosFromDB', todos);
    workWithDB('http://127.0.0.1:8000/api/v1/todos/todos/','GET')
    .then((data) => { 
      callb (data, function () { 
        //setTodos(checkFilter(data, filter))       
        console.log('setTodosFromDB befor', data);
        console.log('setTodosFromDB setTodos befor', todos);
      }) 
    })

  }
  function test () {


  }

  return (    
    <>   
      <Context.Provider value = {{removeTodo}}>
      <div>        
          <div className = 'wrapper'>
            <h1>ToDo List</h1>
            <AddTodo onCreate = {addTodo}/>
            {todos.length ? <TodoList todos = {todos} onToggle = {toggleTodo}/> : <p>no Todo</p>}    
          </div>          
        <div id ='btnPress'> 
          <button  type='submit' onClick = {clearAll} className = 'btnStates'>remove all</button>           
          <button id = 'btnShowAll' type='submit' onClick = {showAll} className = {`btnStates ${filter === 'all' ? 'gradient-button' : ''}`}>show all</button> 
          <button id = 'btnshowWork' type='submit' onClick = {showWork} className = {`btnStates ${filter === 'work' ? 'gradient-button' : ''}`}>show in worke</button> 
          <button id = 'btnShowComp' type='submit' onClick = {showComplited} className = {`btnStates ${filter === 'compl' ? 'gradient-button' : ''}`}>show complited</button>  
        </div> 
        </div>   
      </Context.Provider>
    </>
  );

}

export default App;
