import React from "react";
import TodoList from "./Todo/TodoList";
import Context from "./context";
import AddTodo from "./Todo/AddTodo";


const App = () => {
  const [todos, setTodos] = React.useState([
    {id:1, completed:false, title:'Kupit hleb'},
    {id:2, completed:false, title:'Kupit bred'},
    {id:3, completed:false, title:'Kupit moloko'},
  ])
  //*заполнить из локалсторедж*/
  const toggleTodo = (id) => {
  setTodos(
    todos.map( todo => {
    if (todo.id === id){
      todo.completed = !todo.completed    
    }
    return todo
  }))

}

const removeTodo = (id) => {
  setTodos(todos.filter(todo => todo.id !== id))
}

const clearAll= () => {
  setTodos([]);
}

const showeWork = (id) => {
  setTodos( todos.filter(todo => todo.completed !== true));
  /* к кнопке привязать   */
}/* показать все через локалсторедж , стрелочные сделать,  убрать ченджтуду или проктинуть её.*/

const showeComplited = (id) => {
  setTodos(todos.filter(todo => todo.completed === true));
}

const addTodo = (title) => { 
    setTodos(todos.concat([{
    title,
    id: Date.now(),
    completed:false,
  }]))

}

  return (
    <React.Fragment>
    <Context.Provider value = {{removeTodo}}>
    <div className='wrapper'>
      <h1>React tutorial</h1>
      <AddTodo onCreate={addTodo}/>
      {todos.length ? <TodoList todos={todos} onToggle={toggleTodo}/> : <p>no Todo</p>}    
    </div>
    </Context.Provider>
    <button type='submit' onClick = {clearAll} className='btnStates'>remove all</button> 
     <button type='submit' onClick = {clearAll} className='btnStates'>showe all</button> 
     <button type='submit' onClick = {showeWork} className='btnStates'>showe in worke</button> 
     <button type='submit' onClick = {showeComplited}>showe complited</button>  
    </React.Fragment>
  );
}

export default App;
