import React, { Component, useEffect, useState } from "react";
import TodoList from "./Todo/TodoList";
import Context from "./context";
import AddTodo from "./Todo/AddTodo";
import { element } from "prop-types";



const App = () => { 
  const [todos, setTodos] = useState([]);
  const [filter, setFilter] = useState('all');

  useEffect(() => {
  let arr = [];
    fetch('http://127.0.0.1:8000/api/v1/todos/todos/')
    .then(response => response.json())
    .then((data) => {
      console.log("data1", data);  
     let localArr = {}
    data.forEach(element => {
     localArr = {elementtext, id:element.id, status:element.status,};
     console.log('localARR0',localArr)
     arr.push(localArr);
     console.log('ARR0',arr)     
    });
    console.log('ARR1',arr)  
      setTodos(checkFilter(arr, filter));    
    }) 
  }, []);

  useEffect(() => {
  setTodos(checkFilter(todos, filter));
  }, [filter])

  const toggleTodo = (id) => {  
    // let localArr = [...todos];
    // let a = localArr.map( todo => {
    //   if (todo.id === id) {
    //     todo.status = !todo.status;
    //   }      
    // return todo;
    // });
    // updateLocalStorege(a); 

    setTodos(todos.map(todo => {
      if (todo.id === id) {
        todo.status = !todo.status;
      }      
      return todo;
    }));
  }  

  const checkFilter = (data, todoFilter) => {
      switch (todoFilter) {
        case 'all':  
          return data;       
        case 'work': 
          return data.filter(todo => !todo.status);
        case 'compl': 
          return data.filter(todo => todo.status);
        default:
          break; 
      }
  }

  const removeTodo = (id) => {    
    setTodos(todos.filter(todo => todo.id !== id));  
  }

  const clearAll = () => {
    setTodos([]);
    setFilter('all');
  }

  const showWork = () => {    
    setTodos(todos.filter(todo => !todo.status));
    setFilter('work');
  }

  const showComplited = () => {    
    setTodos(todos.filter(todo => todo.status));
    setFilter('compl');
  }

  const showAll = () => {    
    setFilter('all');
  }

  const addTodo = (title) => { 
    /**обработку с бека */
    setTodos(todos.concat([{        
      id: Date.now(),
      title,
      status: false,
    }]));
  }

  return (    
    <>   
      <Context.Provider value = {{removeTodo}}>
      <div>        
          <div className = 'wrapper'>
            <h1>ToDo List</h1>
            <AddTodo onCreate = {addTodo}/>
            {todos.length ? <TodoList todos = {todos} onToggle = {toggleTodo}/> : <p>no Todo</p>}    
          </div>          
        <div id ='btnPress'> 
          <button  type='submit' onClick = {clearAll} className = 'btnStates'>remove all</button>           
          <button id = 'btnShowAll' type='submit' onClick = {showAll} className = {`btnStates ${filter === 'all' ? 'gradient-button' : ''}`}>show all</button> 
          <button id = 'btnshowWork' type='submit' onClick = {showWork} className = {`btnStates ${filter === 'work' ? 'gradient-button' : ''}`}>show in worke</button> 
          <button id = 'btnShowComp' type='submit' onClick = {showComplited} className = {`btnStates ${filter === 'compl' ? 'gradient-button' : ''}`}>show complited</button>  
        </div> 
        </div>   
      </Context.Provider>
    </>
  );

}


export default App;
