import React, {useContext} from 'react';
import PropTypes from 'prop-types'
import Context from '../context';

const styles = {
     input: {
        marginRight: '1rem'
    }
};

const TodoItem = ({ index, todo, onChange}) => {

    const { removeTodo } = useContext(Context);
    const classes = [];

    if (todo.completed) {
        classes.push('done');
        postData('http://127.0.0.1:8000/api/v1/todos/todos/','UPDATE', true)        
    }
    async function postData(url, meth, data ) {
        // Default options are marked with *
        const response = await fetch(url, {
        method: meth, // *GET, POST, PUT, DELETE, etc.  
        headers: {
          'Content-Type': 'application/json;charset=utf-8',      
        },
        body: JSON.stringify(data), // body data type must match "Content-Type" header
        dataType: 'json',
        });
        if (meth !== 'DELETE' || '')
          return await response.json(); // parses JSON response into native JavaScript objects
      }

    return (
        <li className = 'listyles'>
           <span className = {classes.join(' ')}> 
            <input type = 'checkbox'
                    checked = {todo.completed}
                    style = {styles.input} 
                    onChange = {() => onChange(todo.id)} />  
                <strong>{++index}</strong>
                &nbsp;
                {todo.text}
            </span>
            <button id = 'rmbtn' className = 'rm' onClick = {() => removeTodo(todo.id)}>&times;</button>
        </li>
    )


}

TodoItem.propTypes = {
    todo: PropTypes.object.isRequired,
    index:PropTypes.number,
    onChange:PropTypes.func.isRequired,
}

export default TodoItem
