import React, { Component, useEffect, useState } from "react";
import TodoList from "./Todo/TodoList";
import Context from "./context";
import AddTodo from "./Todo/AddTodo";
import { element } from "prop-types";

const App = () => { 
  const [todos, setTodos] = useState([]);
  const [filter, setFilter] = useState('all');
  const [chtoTo, setChtoTo] = useState(null)
  // a.push({
  //   title,
  //   id: Date.now(),
  //   completed:false,
  // });  

  fetch('http://127.0.0.1:8000/api/v1/todos/users/')
  .then(response => response.json())
  .then((data) => { 
    console.log("data",data)
    postData(data)
    console.log("todos: ",todos)
  })


  const postData = (data) => {

  let localArr = {};

  console.log('change data', localArr) 

    data.forEach(element1 => {
      let localArr = [ {title:element1.text, id:element1.id, completed:element1.status,}];
      console.log('localARR',localArr)
      setTodos
   });

 }

// fetch("http://127.0.0.1:8000/api/v1/todos/users/", {
//   method: 'post',
//   body: JSON.stringify({
//     url: 'http://127.0.0.1:8000/api/v1/todos/users/8/',
//     text: "Title of post",
//     status: true,
//     owner: 'http://127.0.0.1:8000/api/v1/todos/groups/1/',
//   })
// })
//   .then(response => {
//     if (!response.ok) throw Error(response.statusText);
//     return response.json();
//   })
//   .then(data => console.log(data))
//   .catch(error => console.log(error));

  useEffect(() => {
    if (localStorage.length > 0) {
      let locArr = JSON.parse(localStorage.getItem('key'));     
      let arr = [...locArr];
      setTodos(arr);      
      }    
  }, []);

  const toggleTodo = (id) => {  
    let localArr = [...todos];
    let a = localArr.map( todo => {
      if (todo.id === id) {
        todo.completed = !todo.completed;
      }      
    return todo;
    });
    updateLocalStorege(a); 
    setTodos(a);

  }  

  const checkFilter = () => {
      switch (filter) {
        case 'all':  
          return todos;       
        case 'work': 
          return todos.filter(todo => todo.completed !== true);
        case 'compl': 
          return todos.filter(todo => todo.completed === true);
        default:
          break; 
      }
  }

  const removeTodo = (id) => {  
    let localArr = pushTodos();
    let a = [];  

    const localCalc1 = () => {
      a = localArr.filter(localArr => localArr.id !== id) 
      updateLocalStorege(a);
    }

    if (filter === 'all') {
      localCalc1();
    } else {
      localCalc1();
      localArr = [...todos];
      a = localArr.filter(localArr => localArr.id !== id);         
    }   
    setTodos(a);  
  }

  const clearAll = () => {
    localStorage.setItem('key', JSON.stringify([]));  
    setTodos([]);
    setFilter('all');
  }

  const showeWork = (id) => { 
    let localArr = pushTodos();
    setTodos(localArr.filter(todo => todo.completed !== true));
    setFilter('work');
  }

  const showeComplited = (id) => {
    let localArr = pushTodos();
    setTodos(localArr.filter(todo => todo.completed === true));
    setFilter('compl');
  }

  const showeAll = () => {
    let localArr = pushTodos();
    setTodos(localArr);
    setFilter('all');
  }

  const addTodo = (title) => {
    let b = [];
    let a = [];

    const localCalc = () => {
      let arrLocal = [...todos]
      a = [...arrLocal]
      a.push({        
        id: Date.now(),
        title,
        completed:false,
      });    
    }
    if (filter==='all') { 
      localCalc();
      updateLocalStorege(a);    
    } else {
      b = pushTodos();   
      localCalc();  
      b = b.concat(a.slice(a.length -1));     
      updateLocalStorege(b);  
      }  
    setTodos(a);
  }

  const updateLocalStorege = (a) => {     
    localStorage.setItem('key', JSON.stringify(a));    
  }

  const pushTodos = () => {
    let arr=[];
    if (localStorage.length > 0) {
      let locArr = JSON.parse(localStorage.getItem('key'));     
      arr = [...locArr];       
    }
    return arr; 
  }

  return (    
    <>   
      <Context.Provider value = {{removeTodo}}>
      <div>        
          <div className = 'wrapper'>
            <h1>ToDo List</h1>
            <AddTodo onCreate = {addTodo}/>
            {todos.length ? <TodoList todos = {checkFilter} onToggle = {toggleTodo}/> : <p>no Todo</p>}    
          </div>          
        <div id ='btnPress'> 
          <button  type='submit' onClick = {clearAll} className = 'btnStates'>remove all</button>           
          <button id = 'btnShowAll' type='submit' onClick = {showeAll} className = {`btnStates ${filter === 'all' ? 'gradient-button' : ''}`}>show all</button> 
          <button id = 'btnshowWork' type='submit' onClick = {showeWork} className = {`btnStates ${filter === 'work' ? 'gradient-button' : ''}`}>show in worke</button> 
          <button id = 'btnShowComp' type='submit' onClick = {showeComplited} className = {`btnStates ${filter === 'compl' ? 'gradient-button' : ''}`}>show complited</button>  
        </div> 
        </div>   
      </Context.Provider>
    </>
  );
}

export default App;
