import React, { Component, useEffect, useState } from "react";
import TodoList from "./Todo/TodoList";
import Context from "./context";
import AddTodo from "./Todo/AddTodo";
import { element } from "prop-types";



const App = () => { 
  const [todos, setTodos] = useState([]);
  const [filter, setFilter] = useState('all');
  const [chtoTo, setChtoTo] = useState(null)

//   const postData = (data) => {

//   let localArr = {};

//   console.log('change data', localArr) 

//     data.forEach(element1 => {
//       let localArr = [{title:element1.text, id:element1.id, status:element1.status,}];
//       console.log('localARR',localArr)
//       setTodos(localArr);
//    });
//  }

 useEffect(() => {
  let arr = [];
  fetch('http://127.0.0.1:8000/api/v1/todos/todos/')
  .then(response => response.json())
  .then((data) => {
    console.log("data1", data);

    /*
    let localArr = {}
    data.forEach(element => {
     localArr = {text, id:element.id, status:element.status,};
     console.log('localARR0',localArr)
     arr.push(localArr);
     console.log('ARR0',arr)

    });
    console.log('ARR1',arr)*/
    setTodos(checkFilter(data, filter));

  })

}, []);

useEffect(() => {
  setTodos(checkFilter(data, filter));
}, [filter])

  const toggleTodo = (id) => {  
    // let localArr = [...todos];
    // let a = localArr.map( todo => {
    //   if (todo.id === id) {
    //     todo.status = !todo.status;
    //   }      
    // return todo;
    // });
    // updateLocalStorege(a); 

    setTodos(todos.map(todo => {
      if (todo.id === id) {
        todo.status = !todo.status;
      }      
      return todo;
    }));
  }  

  const checkFilter = (data, todoFilter) => {
      switch (todoFilter) {
        case 'all':  
          return data;       
        case 'work': 
          return data.filter(todo => !todo.status);
        case 'compl': 
          return data.filter(todo => todo.status);
        default:
          break; 
      }
  }

  const removeTodo = (id) => {  
    // let localArr = pushTodos();
    // let a = [];  

    // const localCalc1 = () => {
    //   a = localArr.filter(localArr => localArr.id !== id) 
    //   updateLocalStorege(a);
    // }

    // if (filter === 'all') {
    //   localCalc1();
    // } else {
    //   localCalc1();
    //   localArr = [...todos];
    //   a = localArr.filter(localArr => localArr.id !== id);         
    // } 


    setTodos(todos.filter(todo => todo.id !== id));  
  }

  const clearAll = () => {
    localStorage.setItem('key', JSON.stringify([]));  
    setTodos([]);
    setFilter('all');
  }

  const showeWork = (id) => {    
    setTodos(todos.filter(todo => !todo.status));
    setFilter('work');
  }

  const showeComplited = (id) => {    
    setTodos(todos.filter(todo => todo.status));
    setFilter('compl');
  }

  const showeAll = () => {    
    setFilter('all');
  }

  const addTodo = (title) => { 
    /**обработку с бека */
    setTodos(todos.concat([{        
      id: Date.now(),
      title,
      status: false,
    }]));
  }


  const pushTodos = () => {
    let arr=[];
    if (localStorage.length > 0) {
      let locArr = JSON.parse(localStorage.getItem('key'));     
      arr = [...locArr];       
    }
    return arr; 
  }

  return (    
    <>   
      <Context.Provider value = {{removeTodo}}>
      <div>        
          <div className = 'wrapper'>
            <h1>ToDo List</h1>
            <AddTodo onCreate = {addTodo}/>
            {todos.length ? <TodoList todos = {todos} onToggle = {toggleTodo}/> : <p>no Todo</p>}    
          </div>          
        <div id ='btnPress'> 
          <button  type='submit' onClick = {clearAll} className = 'btnStates'>remove all</button>           
          <button id = 'btnShowAll' type='submit' onClick = {showeAll} className = {`btnStates ${filter === 'all' ? 'gradient-button' : ''}`}>show all</button> 
          <button id = 'btnshowWork' type='submit' onClick = {showeWork} className = {`btnStates ${filter === 'work' ? 'gradient-button' : ''}`}>show in worke</button> 
          <button id = 'btnShowComp' type='submit' onClick = {showeComplited} className = {`btnStates ${filter === 'compl' ? 'gradient-button' : ''}`}>show complited</button>  
        </div> 
        </div>   
      </Context.Provider>
    </>
  );

}


export default App;
